<?phpfunction clean_input($string){    $string = trim($string);    $string = mysql_real_escape_string($string);    return $string;}function signed_in(){    if (!isset($_SESSION['signed_in'])){        return FALSE;    } else {        return TRUE;    }}function is_admin($user, $type){    $admin = mysql_fetch_row(mysql_query("SELECT user_level FROM users WHERE user_".$type."='$user'"));    if ($admin[0] >= 2){        return TRUE;    } else {        return FALSE;    }}function user_id_to_name($user_id){    $user_id = mysql_real_escape_string($user_id);    $user_name = mysql_fetch_row(mysql_query("SELECT user_name FROM users WHERE user_id='$user_id'"));    return $user_name[0];}function user_id_to_display_name($user_id){    $user_id = mysql_real_escape_string($user_id);    $user_display_name = mysql_fetch_row(mysql_query("SELECT user_display_name FROM users WHERE user_id='$user_id'"));    return $user_display_name[0];}function resize_img($original_name, $goal_width = 1000, $goal_height = 100){    $image_array = getimagesize($original_name);    $width = $image_array[0];    $height = $image_array[1];    $return = array('width' => $width, 'height' => $height);    if ($width/$height > $goal_width/$goal_height && $width > $goal_width){        $return['width'] = $goal_width;        $return['height'] = $goal_width/$width * $height;    } elseif ($height > $goal_height){        $return['width'] = $goal_height/$height * $width;        $return['height'] = $goal_height;    }    $return = '<img width="'.$return['width'].'" height="'.$return['height'].'" src="'.$original_name.'">';    return $return;}function show_id_to_name($show_id){    $show_id = mysql_real_escape_string($show_id);    $show_name = mysql_fetch_row(mysql_query("SELECT show_name FROM shows WHERE show_id='$show_id'"));    return $show_name[0];}function show_name_to_id($show_name){    $show_name = mysql_real_escape_string($show_name);    $show_id = mysql_fetch_row(mysql_query("SELECT show_id FROM shows WHERE show_name='$show_name'"));    return $show_id[0];}function episode_id_to_name($show_id, $show_season, $episode_id){    $episode_id = mysql_real_escape_string($episode_id);    $episode_name = mysql_fetch_row(mysql_query("SELECT episode_name FROM episodes WHERE show_id='$show_id' AND show_season='$show_season' AND episode_id='$episode_id'"));    return $episode_name[0];}function episode_name_to_id($episode_name){    $episode_name = mysql_real_escape_string($episode_name);    $episode_id = mysql_fetch_row(mysql_query("SELECT episode_id FROM episodes WHERE episode_name='$episode_name'"));    return $episode_id[0];}?>